import React, { useState, useEffect } from 'react';
import './StreamingText.css';

const StreamingText = ({ text, onComplete, transformText }) => {
    const [displayedText, setDisplayedText] = useState('');
    const [isComplete, setIsComplete] = useState(false);

    useEffect(() => {
        let index = 0;
        let timeoutId;
				setDisplayedText("");
				setIsComplete(false);

				const streamNextChar = () => {
					if (index < text.length) {
						setDisplayedText(text.substring(0, index + 1));
						index++;
						timeoutId = setTimeout(streamNextChar, 10);
					} else {
						setIsComplete(true);
						if (onComplete) onComplete();
					}
				};

				timeoutId = setTimeout(streamNextChar, 10);

        return () => {
					if (timeoutId) clearTimeout(timeoutId);
				};
    }, [text, onComplete]);

    const processedText = transformText ? transformText(displayedText) : displayedText;

    return (
        <div 
            className="streaming-text"
            dangerouslySetInnerHTML={{ __html: processedText + (!isComplete ? '<span class="cursor"></span>' : '') }}
        />
    );
};

export default StreamingText;
