class CommandProcessor {
    constructor() {
        this.menuMap = {
            '1': 'about',
            '2': 'skills',
            '3': 'whoami'
        };

        this.navigationStack = ['main'];
        this.commands = {};
    }

    process(input) {
        const command = input.trim().toLowerCase();

        if (command === '') return null;

        if (command === '..') {
            if (this.navigationStack.length > 1) {
                this.navigationStack.pop();
                const previousPage = this.navigationStack[this.navigationStack.length - 1];
                return previousPage === 'main' 
                    ? this.commands.menu.action()
                    : this.commands[previousPage].action();
            }
            return this.commands.menu.action();
        }

        if (this.menuMap[command]) {
            const mappedCommand = this.menuMap[command];
            this.navigationStack.push(mappedCommand);
            return this.commands[mappedCommand].action();
        }

        if (this.commands[command]) {
            if (command !== 'clear') {
                this.navigationStack.push(command);
            }
            return this.commands[command].action();
        }

        return {
            output: null,
            type: 'error'
        };
    }

    setCommands(commands) {
        this.commands = commands;
    }
}

export default CommandProcessor;
