import React, { useState, useEffect, useRef } from 'react';
import StreamingText from '../StreamingText';
import WindowFrame from '../WindowFrame';
import CommandProcessor from '../../services/commandProcessor';
import commands from '../../data/commands';
import { useLanguage } from '../../contexts/LanguageContext.js';
import './Terminal.css';

const Terminal = () => {
    const { t, language, setLanguage } = useLanguage();
    const [history, setHistory] = useState([]);
    const [inputValue, setInputValue] = useState('');
    const [hasShownHelp, setHasShownHelp] = useState(false);
    const terminalRef = useRef(null);
    const inputRef = useRef(null);
    const commandProcessor = useRef(new CommandProcessor());

    useEffect(() => {
        // Set up commands with translations
        const translatedCommands = Object.keys(commands).reduce((acc, key) => {
            acc[key] = {
                ...commands[key],
                action: () => ({
                    type: commands[key].type,
                    output: t(`commands.${key}.output`)
                })
            };
            return acc;
        }, {});

        commandProcessor.current.setCommands(translatedCommands);

        // Initial welcome message
        setHistory([
            {
                id: Date.now(),
                text: t('welcome'),
                type: "system",
                streaming: true,
            },
        ]);
        inputRef.current?.focus();
    }, []);

    const scrollToBottom = () => {
        if (terminalRef.current) {
            terminalRef.current.scrollTop = terminalRef.current.scrollHeight;
        }
    };

    useEffect(() => {
        scrollToBottom();
    }, [history]);

    const handleSubmit = (e) => {
        e.preventDefault();
        if (!inputValue.trim()) return;

        const command = inputValue.trim().toLowerCase();
        let newHistory;

        if (command === 'lang') {
            // Handle language switching
            const newLang = language === 'en' ? 'it' : 'en';
            newHistory = [
                ...history,
                { id: Date.now(), text: inputValue, type: 'prompt' },
                { 
                    id: Date.now() + 1, 
                    text: t('commands.lang.output'), 
                    type: 'success',
                    streaming: true 
                }
            ];
            setLanguage(newLang);
            setHistory(newHistory);
        } else if (command === 'clear') {
            // Handle clear command
            setHistory([]);
            setHasShownHelp(false);
        } else {
            // Handle other commands
            const result = commandProcessor.current.process(command);
            newHistory = [
                ...history,
                { id: Date.now(), text: inputValue, type: 'prompt' },
                { 
                    id: Date.now() + 1, 
                    text: result?.output || t('errors.commandNotFound'), 
                    type: result?.type || 'error',
                    streaming: true 
                }
            ];
            setHistory(newHistory);
        }

        setInputValue('');
    };

    const handleLanguageChange = () => {
        const newLang = language === 'en' ? 'it' : 'en';
        setLanguage(newLang);
        return t('commands.lang.output');
    };

    const handleWelcomeComplete = () => {
        if (!hasShownHelp) {
            const helpResult = commandProcessor.current.process('help');
            if (helpResult) {
                setHistory(prev => [...prev, {
                    id: Date.now(),
                    text: helpResult.output,
                    type: helpResult.type,
                    streaming: true
                }]);
                setHasShownHelp(true);
            }
        }
    };

    return (
        <WindowFrame>
            <div className="terminal" ref={terminalRef} onClick={() => inputRef.current?.focus()}>
                <div className="terminal-content">
                    {history.map(entry => (
                        <div key={entry.id} className={`line ${entry.type}`}>
                            {entry.type === 'prompt' ? (
                                <div className="prompt">{entry.text}</div>
                            ) : entry.streaming ? (
                                <StreamingText 
                                    text={entry.text} 
                                    onComplete={
                                        entry.text.includes("Welcome to my interactive portfolio") 
                                            ? handleWelcomeComplete 
                                            : undefined
                                    }
                                />
                            ) : (
                                <StreamingText text={entry.text} />
                            )}
                        </div>
                    ))}
                    <div className="input-line">
                <span className="input-prefix">{t('commandPrompt')} </span>
                        <div className="input-wrapper">
                            <input
                                ref={inputRef}
                                type="text"
                                value={inputValue}
                                onChange={(e) => setInputValue(e.target.value)}
                                onKeyDown={(e) => {
                                    if (e.key === 'Enter') {
                                        handleSubmit(e);
                                    }
                                }}
                spellCheck="false"
                autoFocus
                autoComplete="off"
                autoCapitalize="none"
                autoCorrect="off"
                            />
                        </div>
                    </div>
                </div>
            </div>
        </WindowFrame>
    );
};

export default Terminal;
