import React, { useState, useEffect, useRef } from 'react';
import StreamingText from '../StreamingText';
import WindowFrame from '../WindowFrame';
import CommandProcessor from '../../services/commandProcessor';
import commands from '../../data/commands';
import './Terminal.css';

const Terminal = () => {
    const [history, setHistory] = useState([]);
    const [inputValue, setInputValue] = useState('');
    const terminalRef = useRef(null);
    const inputRef = useRef(null);
    const commandProcessor = useRef(new CommandProcessor());

    useEffect(() => {
        commandProcessor.current.setCommands(commands);
        // Initial welcome message
        setHistory([
            { 
                id: Date.now(), 
                text: "Hi! I'm Giomarco Baglioni, welcome to my interactive portfolio! ðŸ‘‹\nType 'help' to see available commands or just explore around...", 
                type: 'system',
                streaming: true 
            }
        ]);
        inputRef.current?.focus();
    }, []);

    const scrollToBottom = () => {
        if (terminalRef.current) {
            terminalRef.current.scrollTop = terminalRef.current.scrollHeight;
        }
    };

    useEffect(() => {
        scrollToBottom();
    }, [history]);

    const handleSubmit = (e) => {
        e.preventDefault();
        if (!inputValue) return;

        const result = commandProcessor.current.process(inputValue);

        if (result?.type === 'clear') {
            setHistory([]);
        } else {
            if (!['help', 'clear'].includes(inputValue.toLowerCase())) {
                setHistory([
                    { id: Date.now() - 2, text: inputValue, type: 'prompt' },
                    { 
                        id: Date.now() - 1, 
                        text: result?.output || 'Command not found', 
                        type: result?.type || 'error',
                        streaming: true 
                    }
                ]);
            } else {
                const newHistory = [
                    ...history,
                    { id: Date.now(), text: inputValue, type: 'prompt' },
                    { 
                        id: Date.now() + 1, 
                        text: result?.output || 'Command not found', 
                        type: result?.type || 'error',
                        streaming: true 
                    }
                ];
                setHistory(newHistory);
            }
        }

        setInputValue('');
    };

    return (
        <WindowFrame>
            <div className="terminal" ref={terminalRef} onClick={() => inputRef.current?.focus()}>
                <div className="terminal-content">
                    {history.map(entry => (
                        <div key={entry.id} className={`line ${entry.type}`}>
                            {entry.type === 'prompt' ? (
                                <div className="prompt">{entry.text}</div>
                            ) : entry.streaming ? (
                                <StreamingText 
                                    text={entry.text} 
                                    onComplete={() => {
                                        if (entry.text.includes("Hi! I'm Giomarco")) {
                                            const helpResult = commandProcessor.current.process('help');
                                            if (helpResult) {
                                                setHistory(prev => [...prev, {
                                                    id: Date.now(),
                                                    text: helpResult.output,
                                                    type: helpResult.type,
                                                    streaming: true
                                                }]);
                                            }
                                        }
                                    }}
                                />
                            ) : (
                                <StreamingText text={entry.text} />
                            )}
                        </div>
                    ))}
                    <div className="input-line">
                        <span className="input-prefix">guest@portfolio:~$ </span>
                        <div className="input-wrapper">
                            <input
                                ref={inputRef}
                                type="text"
                                value={inputValue}
                                onChange={(e) => setInputValue(e.target.value)}
                                onKeyDown={(e) => {
                                    if (e.key === 'Enter') {
                                        handleSubmit(e);
                                    }
                                }}
                                spellCheck="false"
                                autoFocus
                            />
                        </div>
                    </div>
                </div>
            </div>
        </WindowFrame>
    );
};

export default Terminal;
